package day3;

public class Increementoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// ++ is call increment
		
		int a =101;
	//	System.out.println(a);
	int res =	a++;
		System.out.println(res);
		System.out.println(a);
		
		// -- is call Decrement
		
		int b =100;
		System.out.println(b);
		b--;
		System.out.println(b);
	}

}
